def reverse_words(s: str) -> str:
    """
    Uses a stack to reverse a string.
    >>> reverse_words('applebananaapple')
    'applebananapingelap'
    >>> reverse_words('apple')
    'apple'
    >>> reverse_words('')
    ''
    >>> reverse_words('     ')
    ''
    """
    stack = []
    for c in s:
        if c.isspace():
            word = "".join(stack)
            stack.clear()
            if word != "":
                stack.append(word)
        else:
            stack.append(c)
    word = "".join(stack)
    stack.clear()
    if word != "":
        stack.append(word)
    return "".join(stack[::-1])


