  if(len(l) == 0):
      return False
  if(len(l) == 1):
      return False
  if(len(l) == 2):
      if(l[0] + l[1] == 0):
          return True
      else:
          return False
  for i in range(len(l) - 1):
      for j in range(i + 1, len(l)):
          if(l[i] + l[j] == 0):
              return True
  return False


